import React from 'react';
import { Button, Col, Row } from 'react-bootstrap';

const Category = ({filter}) => {

    const onFilter = (cat) => {
        filter(cat)
    }
    return (
        <Row className='my-3 mb-3'>
            <Col sm="12" className='justify-content-center d-flex'>
                <div>
                    <Button  style={{ border: '2px solid black', width: "13vh"  }} className='btn mx-2'>Menu</Button>
                    <Button  style= {{ border: '2px solid black' }}className='btn mx-2'>Petit-Déjeuné</Button>
                    <Button onClick={()=> onFilter('Déjeuné')} style= {{ border: '2px solid black' }} className='btn mx-2'>Déjeuné</Button>
                    <Button  onClick={()=> onFilter('dîner')}style= {{ border: '2px solid black' }} className='btn mx-2'>Menu du Soir</Button>
                </div>

            </Col>
        </Row>
    );
};

export default Category;
