import React from 'react';
import { Card, Col, Row } from 'react-bootstrap';
import image from "../assets/plat.png"
const Cards = () => {
    return (

    <Row>
        <Col sm="12" className='mb-2' >
            <Card className='d-flex flex-row w-100'>
                <Card.Img variant="top" src={image}/>
                    <Card.Body>
                    <Card.Title>
                        <div className='cards-title'>Poulet Roti</div>
                    </Card.Title>
                    <Card.Text>
                        Un plat à base de poulet cuit dans une rôtissoire, un four, un barbecue, en utilisant la chaleur directe de l'appareil dans lequel est placé le poulet. Des éléments chauffants électriques ou à gaz peuvent être utilisés,
                        qui utilisent une chaleur infrarouge réglable
                    </Card.Text>
                </Card.Body>
            </Card>
        </Col>
    </Row>

    );
}

export default Cards;
